documentation.xhtml:
CoEReadIndex : ARRAY[1..50] OF WORD;
CoEReadSubIndex : ARRAY[1..50] OF BYTE;
CoEReadSlaveAddr : ARRAY[1..50] OF UINT;

ST.xhtml:
EtherCAT_Diagnostics();

SetIpAddress();
stArrEcConfSlaveInfo[1].sName;
stArrEcConfSlaveInfo[1].sType;
stArrEcConfSlaveInfo[1].nAddr;

CASE StepCoERead OF //Cyclicly read CoE data to send back to rockwell controller.
	0: //This step is important because it sets all the pointer values. 

		CoEReadintPos:=0;
		//Not implimented yet.
		CoEReadPos:=1;
		StepCoERead:=StepCoERead+10;
		//Update Pointers
		FOR i:=1 TO  CoEReadTotalCount BY 1 DO
			IF CoEReadDataType[i] = 'INT' THEN
				CoEReadintPos:=CoEReadintPos+1;
				CoEReadData[i]:=ADR(CoEReadInt[CoEReadintPos]);
				CoEReadcbBufLen[i]:=SIZEOF(CoEReadInt[CoEReadintPos]);
			ELSIF CoEReadDataType[i] = 'BYTE' THEN
				CoEReadbytePos:=CoEReadbytePos+1;
				CoEReadData[i]:=ADR(CoEReadByte[CoEReadbytePos]);
				CoEReadcbBufLen[i]:=SIZEOF(CoEReadByte[CoEReadbytePos]);
			ELSIF CoEReadDataType[i] = 'UINT' THEN
				CoEReaduintPos:=CoEReaduintPos+1;
				CoEReadData[i]:=ADR(CoEReadUint[CoEReaduintPos]);
				CoEReadcbBufLen[i]:=SIZEOF(CoEReadUint[CoEReaduintPos]);
			ELSIF CoEReadDataType[i] = 'UDINT' THEN
				CoEReadudintPos:=CoEReadudintPos+1;
				CoEReadData[i]:=ADR(CoEReadUdint[CoEReadudintPos]);
				CoEReadcbBufLen[i]:=SIZEOF(CoEReadUdint[CoEReadudintPos]);
			ELSIF CoEReadDataType[i] = 'BOOL' THEN
				CoEReadboolPos:=CoEReadboolPos+1;
				CoEReadData[i]:=ADR(CoEReadBool[CoEReadboolPos]);
				CoEReadcbBufLen[i]:=SIZEOF(CoEReadBool[CoEReadboolPos]);
			END_IF		
		END_FOR


	5: //Pause cycle so CoE reads are not constantly occuring.
		CoEReadTonUpdateRate(IN:=TRUE, PT:=T#100MS);	
		IF CoEReadTonUpdateRate.Q THEN
			StepCoERead:=10;
		END_IF

	10: //Increment index pointer for next read target.
		fbCoERead( bExecute:=FALSE);
		IF fbCoERead.bBusy = FALSE THEN
			IF CoEReadPos > CoEReadTotalCount THEN
				CoEReadPos:=1;	
				StepCoERead:=5;
				CoEReadTonUpdateRate(IN:=FALSE);		
			ELSE
				IF STRING_TO_UINT(CoEReadSlaveAddress[CoEReadPos]) > 0 AND CoEReadSubIndex[CoEReadPos] > 0  THEN
					//We have something to read		
					StepCoERead:=StepCoERead+10;			
				ELSE
					CoEReadPos:=1;	
					StepCoERead:=5;
					CoEReadTonUpdateRate(IN:=FALSE);		
				END_IF

			END_IF
		END_IF

	20: //Start read
	fbCoERead(
		sNetId:=F_CreateAmsNetId(nIds := GVL_AutoGenerated.EtherCAT_NetId) , 
		nSlaveAddr:=STRING_TO_UINT(CoEReadSlaveAddress[CoEReadPos]) , 
		nSubIndex:=CoEReadSubIndex[CoEReadPos] , 
		nIndex:=CoEReadIndex[CoEReadPos] , 
		pDstBuf:=CoEReadData[CoEReadPos] , 
		cbBufLen:=CoEReadcbBufLen[CoEReadPos] , 
		bExecute:=TRUE , 
		tTimeout:= , 
		bCompleteAccess:= , 
		bBusy=> , 
		bError=> , 
		nErrId=> , 
		cbRead=> );
	IF NOT fbCoERead.bBusy THEN
		IF fbCoERead.bError THEN
			CoEReadErrorCounter:=CoEReadErrorCounter+1;
			CoEReadErrorIndex:=CoEReadPos;
		END_IF
		CoEReadPos:=CoEReadPos+1;
		StepCoERead:=StepCoERead-10;
	END_IF
END_CASE


CASE Step OF
	0:
		//Not implimented yet.

	100:
		IF GVL_AutoGenerated.EtherCAT_CoEWriteExecute THEN
			Step:=Step+10;
		END_IF


	110:
	FB_EcCoESdoWrite1(
		sNetId:= , 
		nSlaveAddr:= , 
		nSubIndex:= , 
		nIndex:= , 
		pSrcBuf:= , 
		cbBufLen:= , 
		bExecute:= , 
		tTimeout:= , 
		bBusy=> , 
		bError=> , 
		nErrId=> );

	120:
		GVL_AutoGenerated.EtherCAT_CoEWriteDone:=TRUE;
END_CASE

IF EtherCATChangeCountReset THEN
		FOR x :=0 TO EtherDeviceCount BY 1 DO
		dwArrSlaveChangeCountHistory[x]:=dwArrSlaveChangeCount[x];
	END_FOR
END_IF


CASE StepReadEtherCATDiagnostics OF
0:
	FB_EcGetAllSlavePresentStateChanges.bExecute:=FALSE;
	fbGetAllSlaveStates.bExecute:=FALSE;
	fbEcGetConfSlaves.bExecute:=FALSE;
	//fbEcGetAllSlaveCrcErrors.bError:=FALSE;
	TonReadDiagnostics(IN:=TRUE, PT:=T#1S);
	IF TonReadDiagnostics.q THEN
		TonReadDiagnostics(IN:=FALSE);
		StepReadEtherCATDiagnostics:=StepReadEtherCATDiagnostics+10;
		FB_EcGetAllSlavePresentStateChanges.bExecute:=TRUE;
		fbGetAllSlaveStates.bExecute:=TRUE;
		fbEcGetConfSlaves.bExecute:=TRUE;
		//fbEcGetAllSlaveCrcErrors.bError:=TRUE;
	END_IF

10:
	IF NOT FB_EcGetAllSlavePresentStateChanges.bBusy THEN
		StepReadEtherCATDiagnostics:=StepReadEtherCATDiagnostics+10;
		IF NOT FB_EcGetAllSlavePresentStateChanges.bError THEN		
			;
		ELSE
			sError:='fbEcGetAllSlaveAbnormalStateChanges';
		END_IF
	END_IF

20:
	IF NOT fbGetAllSlaveStates.bBusy THEN
		StepReadEtherCATDiagnostics:=StepReadEtherCATDiagnostics+10;
		IF fbGetAllSlaveStates.bError THEN
			sError:='fbGetAllSlaveStates';
		END_IF
	END_IF

30:
	IF NOT fbEcGetConfSlaves.bBusy THEN
		StepReadEtherCATDiagnostics:=StepReadEtherCATDiagnostics+10;
		IF fbEcGetConfSlaves.bError THEN
			sError:='fbEcGetConfSlaves';
		END_IF
	END_IF

40:
	IF NOT fbEcGetAllSlaveCrcErrors.bBusy THEN
		StepReadEtherCATDiagnostics:=StepReadEtherCATDiagnostics+10;
		IF fbEcGetAllSlaveCrcErrors.bError THEN			
			sError:='fbEcGetAllSlaveCrcErrors';
		END_IF
	END_IF

50:	//Use this step to copy all the values to PDO to be sent to the EIP network
	StepReadEtherCATDiagnostics:=0;
	FOR x :=0 TO EtherDeviceCount-1 BY 1 DO
	GVL_AutoGenerated.EtherCATChangeCount[x+1]:=DWORD_TO_UINT(dwArrSlaveChangeCount[x] - dwArrSlaveChangeCountHistory[x]);
	GVL_AutoGenerated.EtherCATState[x+1]:=DWORD_TO_UINT(stSlaveStates[x].deviceState);
	END_FOR
END_CASE


